    if (location.hash === '') {
      svg4everybody();
    } else if (location.hash === '#fill') {
      svg4everybody({
        polyfill: true
      });
    } else if (location.hash === '#oldie') {
      svg4everybody({
        nosvg: true,
        polyfill: true
      });
    }
    
    function addSVG(node, href) {
      node.innerHTML ='<' + 'svg role="presentation"' + '><' + 'use xlink:href="' + href + '"/' + '><' + '/svg' + '>';
      node.parentNode.replaceChild(node.lastChild, node);
    }

